import React, { Component } from 'react'
import { Mutation } from 'react-apollo'

import uuidv4 from 'uuid/v4'

import { ADD_OWNERS, GET_OWNERS } from '../queries'

import { Button, TextField} from '@material-ui/core'
import { border } from '@material-ui/system';

class AddOwner extends Component {
  state = {
    firstName: '',
    lastName: ''
  }

  render() {
    const { firstName, lastName } = this.state
    const id = uuidv4()
    return (
      <Mutation
        mutation={ADD_OWNERS}
        update={(store, { data: { addOwner } }) => {
          const { owners } = store.readQuery({ query: GET_OWNERS })
          store.writeQuery({
            query: GET_OWNERS,
            data: { owners: owners.concat([addOwner])}
          })
        }}
      >
        {(addOwner, { data, loading, error }) => (
          <form onSubmit={e => {
            e.preventDefault()
            addOwner({
              variables: {
                id,
                firstName,
                lastName
              },
              optimisticResponse: {
                __typename: 'Mutation',
                addOwner: {
                  __typename: 'Owner',
                  id,
                  firstName,
                  lastName
                }
              }
            })
          }}>
            <TextField
              label='First Name'
              value={firstName}
              placeholder='i.e. John'
              onChange={e => this.setState({ firstName: e.target.value })}
              margin='normal'
              varian='outlined'
              style={{ margin: '5px',  border: '2px solid grey'  }}
            />
            <TextField
              label='Last Name'
              value={lastName}
              placeholder='i.e. Smith'
              onChange={e => this.setState({ lastName: e.target.value })}
              margin='normal'
              varian='outlined'

              style={{ 
                margin: '5px',border: '2px solid grey'}}
            />
            <Button
              type='submit'
              variant='contained'
              color='primary'
              style={{ margin: '5px' }}
            >
              Add Owner
            </Button>
          </form>
        )}
      </Mutation>
    )
  }
}

export default AddOwner
